import React, {useState} from 'react'
import { store_products } from '../../Assets/data'
import StoreProductDisplay from '../StoreProductDisplay'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Cart from '../Cart'

export default function Store() {
    let [order, setOrder] = useState([])
    let [orderSubtotal, setOrderSubtotal] = useState(0)

    let updateOrder = (newItem, amount) => {
        order.forEach((item, index) => {
            if (item.item.name == newItem.name) {
                order.splice(index, 1)
            }
        })
        if (!amount == '') {
            setOrder([
                ...order,
                {item: newItem,
                amount: amount}
            ])
        } else {
            setOrder([
                ...order
            ])
        }
        console.log(order)

    }   
    return (
        <Container>
            <Row>
                <Col lg={{span: 11}}>
                    {store_products.map((product) => {
                        return <StoreProductDisplay name={product['name']} price={product['price']} RRP={product['RRP']} updateOrder={updateOrder} />
                    })}
                </Col>
                <Col lg={{span: 1}} style={{backgroundColor: 'white'}}>
                    <Cart orderedItems={order}/>
                </Col>

            </Row>
        </Container>
    )
}
