import React, { useState } from 'react'


export default function Cart({ orderedItems }) {
    console.log('ordered Items for this page', orderedItems)
    let [loading, setLoading] = useState(true)
    return (
        <div>
            {orderedItems.map(order => {
                console.log('Order', order)
                return <div>
                    <p>{order.name}</p>
                    <p>${order.price.toFixed(2)}</p>
                    <p>{order.amount}</p>

                </div>
                {setLoading(false)}
            })}
            <p>Order Subtotal: </p>
        </div>
    )
}
