import React, { useState } from 'react'
import { store_products } from '../../Assets/data'
import StoreProductDisplay from '../StoreProductDisplay'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Cart from '../Cart'

export default function Store() {
    let [order, setOrder] = useState([])
    let [orderSubtotal, setOrderSubtotal] = useState(0)
    let updateOrder = (newItem, amount) => {

        let objectToInsert = { item: newItem, amount: amount }
        order = removeDuplicates(objectToInsert)
        console.log('Order in updateOrder:', order)
        // setOrderSubtotal(order.reduce((previousValue, currentValue) => previousValue + currentValue.item.price), 0)
        setOrder([order])

    }
    let removeDuplicates = (objectToInsert) => {

        order.forEach((item, index) => {
            console.log('THis item', item)
            if (item[0].item.id == objectToInsert.item.id) {
                order[index] = objectToInsert
            }
        })
        if (order.length == 0) {order.push(objectToInsert)}
        return order
    }

    return (
        <Container>
            <Row>
                <Col lg={{ span: 11 }}>
                    {store_products.map((product) => {
                        return <StoreProductDisplay id={product['id']} name={product['name']} price={product['price']} RRP={product['RRP']} removeDuplicates={removeDuplicates} updateOrder={updateOrder} />
                    })}
                </Col>
                <Col lg={{ span: 1 }} style={{ backgroundColor: 'white' }}>
                    <Cart orderedItems={order} orderSubtotal={orderSubtotal} />
                </Col>

            </Row>
        </Container>
    )
}
