import React, { useState } from 'react'


export default function Cart({ orderedItems }) {
    let  [orderSubtotal, setOrderSubtotal] = useState(0)
    return (
        <div>
            {orderedItems.map(order => {
                    setOrderSubtotal(orderSubtotal + order.amount)
                return <div>
                    <p>{order.item.name}</p>
                    <p>${order.item.price.toFixed(2)}</p>
                    <p>{order.amount}</p>

                </div>
            })}
            <p>Subtotal: ${orderSubtotal}</p>
        </div>
    )
}
